import { Component, Input } from '@angular/core';
import { Movie } from '../../models/movie';

@Component({
  selector: 'movie-item',
  templateUrl: './movie-item.component.html',
  styleUrls: ['./movie-item.component.scss']
})
export class MovieItemComponent {
  @Input() itemData: Movie | null = null;

  constructor
}
<a  class="movie-item">
    <div class="movie-item-rating">
        <i class="app-icon">star</i>
        <span><pre>{{itemData | json}}</pre></span>/10

    </div>
    <div class="movie-item-image">
        <img src="https://image.tmdb.org/t/p/original//xDMIl84Qo5Tsu62c9DGWhmPI67A.jpg" alt="">
    </div>
    <div class="movie-item-title">The Vault</div>
</a>
