import { Component, Input, OnInit } from '@angular/core';
import { Movie } from '../../models/movie';
import { IMAGES_SIZE } from '../../constants/images-sizes';

@Component({
  selector: 'movie-item',
  templateUrl: './movie-item.component.html',
  styleUrls: ['./movie-item.component.scss']
})
export class MovieItemComponent implements OnInit {
  @Input() itemData: Movie | null = null;
  imagesSizes = IMAGES_SIZE;

  constructor() {}

  ngOnInit(): void {}
}
<ng-container *ngIf="itemData">
  <a class="movie-item">
    <div class="movie-item-rating">
      <i class="app-icon">star</i>
      <span>{{ itemData.vote_average }}</span
      >/10
    </div>
    <div class="movie-item-image">
      <img [src]="imagesSizes.me + itemData.poster_path" [alt]="itemData.title" />
    </div>
    <div class="movie-item-title">{{ itemData.title }}</div>
  </a>
</ng-container>
