import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Movie, MovieVideo } from 'src/app/models/movie';
import { MoviesService } from 'src/app/services/movies.service';
import { IMAGES_SIZES } from '../../constants/images-sizes';

@Component({
  selector: 'app-movie',
  templateUrl: './movie.component.html',
  styleUrls: ['./movie.component.scss']
})
export class MovieComponent implements OnInit {
  constructor(private route: ActivatedRoute, private movieService: MoviesService) {}

  movie: Movie | null = null;
  imagesSizes = IMAGES_SIZES;
  movieVideos: MovieVideo[] = [];

  ngOnInit(): void {
    this.route.params.subscribe((params) => {
      this.getMovie(params.id);
      this.getMovieVideos(params.id);
    });
  }

  getMovie(id: string) {
    this.movieService.getMovie(id).subscribe((movie) => {
      this.movie = movie;
    });
  }

  getMovieVideos(id:string){
     this.movieService.getMovieVideos(id).subscribe((movieVideoData)=>{
      this.movieVideos = movieVideoData;
     }) 
  }
}
<ng-container *ngIf="movie">
  <slider [items]="[movie]" [isBanner]="true"></slider>
  <p-tabView>
    <p-tabPanel header="Overview">
      <div class="grid">
        <div class="col-12 md:col-3 lg:col-3">
          <img class="poster" [src]="imagesSizes.medium + movie.poster_path" [alt]="movie.title" />
        </div>

        <div class="col-12 md:col-9 lg:col-9">
          <h2>Overview</h2>
          <p class="overview">{{ movie.overview }}</p>
          <div class="stat">
            <div class="label">Release Date:</div>
            <div class="value">
              {{ movie.release_date | date : 'dd.MM.yyyy' }}
            </div>
          </div>
          <div class="stat">
            <div class="label">Language:</div>
            <div class="value">
              {{ movie.original_language | uppercase }}
            </div>
          </div>
          <div class="stat">
            <div class="label">Revenue:</div>
            <div class="value">
              {{ movie.revenue | currency }}
            </div>
          </div>
          <div class="stat">
            <div class="label">Duration:</div>
            <div class="value">{{ movie.runtime }} min</div>
          </div>
          <div class="stat">
            <div class="label">Status:</div>
            <div class="value">
              {{ movie.status }}
            </div>
          </div>
          <div class="stat">
            <div class="label">Genres:</div>
            <div class="value">
              <span *ngFor="let genre of movie.genres"> {{ genre.name }}</span>
            </div>
          </div>
        </div>
      </div>
    </p-tabPanel>
    <p-tabPanel header=" Videos">
      <ng-container *ngFor="let movieVideo of movieVideos">
         <video-embed [key]="movieVideo.key" [site]="movieVideo.key">

         </video-embed></ng-container>

    </p-tabPanel>
    <p-tabPanel header=" Photos"> Photos </p-tabPanel>
  </p-tabView>
</ng-container>
