import { Component, Input, OnInit } from '@angular/core';
import { Movie } from '../../models/movie';
import { IMAGES_SIZES } from '../../constants/images-sizes';
@Component({
  selector: 'item',
  templateUrl: './item.component.html',
  styleUrls: ['./item.component.scss']
})
export class ItemComponent implements OnInit {
  @Input() itemData: Movie | null = null;

  imagesSizes = IMAGES_SIZES;

  constructor() {}

  ngOnInit(): void {}
}
<ng-container *ngIf="itemData">
  <a class="item" [routerLink]="'/movie/' + itemData.id">
    <div class="item-rating">
      <i class="app-icon">star</i>
      <span>{{ itemData.vote_average }}</span
      >/10
    </div>
    <div class="item-image">
      <img [src]="imagesSizes.medium + itemData.poster_path" [alt]="itemData.title" />
    </div>
    <div class="item-title">{{ itemData.title }}</div>
  </a>
</ng-container>
